import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { FaMapMarkerAlt } from "react-icons/fa";

export default function GroundSelection() {
  const [grounds, setGrounds] = useState([]); // Initial state as an array
  const location = useLocation();
  const [userEmail, setUserEmail] = useState("");

  const parseJwt = (token) => {
    if (!token) return null;
    try {
      const base64Url = token.split(".")[1];
      const base64 = decodeURIComponent(
        atob(base64Url)
          .split("")
          .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
          .join("")
      );
      return JSON.parse(base64);
    } catch (error) {
      console.error("Error parsing JWT", error);
      return null;
    }
  };

  useEffect(() => {
    const token = sessionStorage.getItem("authToken");

    if (token) {
      const decodedToken = parseJwt(token);
      if (decodedToken && decodedToken.email) {
        setUserEmail(decodedToken.email);
      } else {
        alert("Unable to decode token or email is missing in token");
      }
    } else {
      alert("No token found. Please log in.");
    }

    // Fetch ground data from the backend
    const fetchGrounds = async () => {
      try {
        // Retrieve the token
        const token = localStorage.getItem("token");
        if (!token) {
          console.error("Token not found!");
          return;
        }

        const response = await fetch("http://127.0.0.1:8000/ground_list/", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Add token here
          },
        });

        if (!response.ok) {
          throw new Error(`${response.status}: ${response.statusText}`);
        }

        const data = await response.json();
        console.log(data); // Debugging

        if (Array.isArray(data)) {
          setGrounds(data); // Update state
        } else {
          console.error("Unexpected data format:", data);
        }
      } catch (error) {
        console.error("Failed to fetch:", error);
      }
    };

    fetchGrounds();
  }, []);

  const hiddenPaths = [
    "/authpageownerlogin",
    "/authpageownersignup",
    "/revenuegrounddetails",
    "/reservations",
    "/viewprofile",
    "/help",
  ];

  const isHiddenPath = hiddenPaths.includes(location.pathname);

  return (
    <>
      {!isHiddenPath && (
        <div className="container my-4">
          <h1 className="email mb-5" style={{ color: "rgb(57 171 148)" }}>
            Select Ground
          </h1>
          <div className="d-flex flex-column justify-content-center align-items-center">
            {Array.isArray(grounds) &&
              grounds.map((ground) => (
                <div
                  key={ground.id}
                  className="ground-data my-3 px-5 py-3 d-flex flex-row justify-content-center align-items-center"
                >
                  <img
                    src={ground.photos[0]}
                    className="image-ground-selection"
                    alt={ground.name}
                  />
                  <div className="d-flex flex-column justify-content-center align-items-start px-5">
                    <div className="signup text-center">
                      <Link
                        to={`/reservations/${ground.id}`}
                        className="btn-ground"
                      >
                        <b>{ground.name}</b>
                      </Link>
                    </div>
                    <div className="loc">
                      <p>{ground.location}</p>
                      <FaMapMarkerAlt className="icon ms-2" />
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}
    </>
  );
}
