import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";
import Calendar from "./Calender";

export default function Reservations() {
  const location = useLocation();
  const [selectedDate, setSelectedDate] = useState(null);
  const [showFilteredBookings, setShowFilteredBookings] = useState(false);
  const [filteredBookings, setFilteredBookings] = useState([]);
  const [bookings, setBookings] = useState([]);

  // Fetch bookings from the database on component mount
  useEffect(() => {
    const token = sessionStorage.getItem("authToken");

    if (!location.state || !location.state.groundID) {
      alert("Ground ID is missing. Redirecting...");
      return <Navigate to="/authpageownerlogin" replace />;
    }

    ground_id = location.state.groundID

    if (!token) {
      alert("No authentication token found. Redirecting to login.");
      return <Navigate to="/authpageownerlogin" replace />; // Redirect to login page if no token
    }
    console.log("token recieved")

    const fetchBookings = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/bookings/${parseInt(ground_id)}`, {
          headers: {
            Authorization: `Bearer ${token}`,  // Send the token in the Authorization header
          },
        });
        setBookings(response.data);
        console.log("API Response:", response.data);

      } catch (error) {
        console.error("Error fetching bookings:", error);
        alert("Unauthorized access. Please log in again.");
        window.location.href = "/authpageownerlogin"; // Redirect if token is invalid
      }
    };
    fetchBookings();
  }, [location.state.groundID]); // Empty dependency array to run on mount only

  // const formatDate = (date) => {
  //   return `${date.getDate()} ${date.toLocaleString("default", {
  //     month: "short",
  //   })} ${date.getFullYear()}`;
  // };

  function parseDateTimeToTime(datetimeStr) {
    const [date, time] = datetimeStr.split('T');
    return time;
  }

  const parseTime = (timeString) => {
    const time = parseDateTimeToTime(timeString)
    let [hours, minutes, seconds] = time.split(":").map(Number);
    time = new Date(0, 0, 0, hours, minutes, seconds);
    console.log(time)
    return time;
  };

  function formatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;

    return [year, month, day].join('-');
}

  const today = formatDate(new Date());

  const upcomingBookings = bookings.filter((booking) => {
    const bookingDate = new Date(booking.booking_date);
    const todayDate = today
    return bookingDate >= todayDate;
  });

  const sortedBookings = upcomingBookings.sort((a, b) => {
    const dateA = new Date(a.booking_date);
    const dateB = new Date(b.booking_date);
    if (dateA - dateB === 0) {
      return (
        parseTime(a.start_time.split(" - ")[0]) -
        parseTime(b.start_time.split(" - ")[0])
      );
    }
    return dateA - dateB;
  });

  // const sortedBookings = upcomingBookings.sort((a, b) => {
  //   const formattedDateA = formatDate(a.booking_date);
  //   const formattedDateB = formatDate(b.booking_date);

  //   // Compare dates first
  //   const dateComparison = formattedDateA.localeCompare(formattedDateB);
  //   if (dateComparison !== 0) {
  //     return dateComparison;
  //   }

  //   // If dates are equal, compare timings
  //   const timeA = parseTime(a.start_time.split(" - ")[0]);
  //   const timeB = parseTime(b.start_time.split(" - ")[0]);
  //   return timeA - timeB;
  // });

  const handleShowSlotsClick = () => {
    console.log("clickkkkkkkkkkk")
    if (selectedDate) {
      // const formattedDate = formatDate(selectedDate);
      const filtered = bookings
        // .filter((booking) => booking.booking_date === formattedDate)
        // .sort(
        //   (a, b) =>
        //     parseTime(a.start_time.split(" - ")[0]) -
        //     parseTime(b.start_time.split(" - ")[0])
        // );
      setFilteredBookings(filtered);
      setShowFilteredBookings(true);
      console.log(sortedBookings)
    } else {
      alert("Please select a date from the calendar!");
    }
  };

  const handlePaymentStatusChange = async (id, newStatus) => {
    try {
      // Send an update request to the backend
      await axios.patch(`http://localhost:5000/api/bookings/${location.state.groundID}`, {
        payment: newStatus,
      });

      // Update the local state
      const updatedBookings = bookings.map((booking) =>
        booking.id === id ? { ...booking, payment: newStatus } : booking
      );
      setBookings(updatedBookings);
    } catch (error) {
      console.error("Error updating payment status:", error);
    }
  };

  return (
    <>
      <div className="nav-style">
        <div className="nav-underline"></div>
      </div>

      {location.pathname !== "/authpageownerlogin" &&
        location.pathname !== "/authpageownersignup" &&
        location.pathname !== "/revenuegrounddetails" &&
        location.pathname !== "/viewprofile" &&
        location.pathname !== "/help" && (
          <>
            <div className="container my-5">
              <h2 className="pt-5" style={{ color: "#55ad9b" }}>
                Upcoming Bookings
              </h2>
              <div className="reservations">
                <div className="reservations-list">
                  {sortedBookings.length > 0 ? (
                    sortedBookings.map((booking) => (
                      <div
                        key={booking.id}
                        className="upcoming-booking p-3 border mb-2"
                      >
                        <h5 style={{ color: "#55ad9b" }}>{booking.pitch_name}</h5>
                        <span>
                          <b>Timing:</b> {booking.start_time}
                          <br />
                        </span>
                        <span>
                          <b>Date:</b> {booking.booking_date}
                          <br />
                        </span>
                        <span>
                          <b>Booked By:</b> {booking.user_email}
                          <br />
                        </span>
                        <span>
                          <b>Contact:</b> {booking.user_contact_no}
                          <br />
                        </span>
                        <span>
                          <b>Status:</b>{" "}
                          {booking.payment_status ? (
                            <span>{booking.payment_status}</span>
                          ) : (
                            <>
                              <button
                                onClick={() =>
                                  handlePaymentStatusChange(booking.id, "Paid")
                                }
                                className="btn btn-success btn-sm me-2"
                              >
                                Paid
                              </button>
                              <button
                                onClick={() =>
                                  handlePaymentStatusChange(
                                    booking.id,
                                    "UnPaid"
                                  )
                                }
                                className="btn btn-warning btn-sm"
                              >
                                UnPaid
                              </button>
                            </>
                          )}
                          <br />
                        </span>
                      </div>
                    ))
                  ) : (
                    <div className="no-booking-message text-center mt-3">
                      <h5 style={{ color: "#0d705c" }}>No upcoming Booking!</h5>
                    </div>
                  )}
                </div>
                <div className="calender">
                  <h2 className="mb-5" style={{ color: "#55ad9b" }}>
                    Check Upcoming Bookings
                  </h2>
                  <Calendar
                    selectedDate={selectedDate}
                    setSelectedDate={setSelectedDate}
                  />
                  <div className="booked-slots">
                    <button
                      className="my-5 slots-button dropdown btn-back"
                      onClick={handleShowSlotsClick}
                    >
                      SHOW SLOTS STATUS
                    </button>
                  </div>

                  {/* Filtered Bookings Popup */}
                  {showFilteredBookings && (
                    <div className="overlay-bookings">
                      <div className="dropdown-bookings mt-3">
                        <button
                          className="close-button"
                          onClick={() => setShowFilteredBookings(false)}
                        >
                          &times;
                        </button>
                        {filteredBookings.length > 0 ? (
                          filteredBookings.map((booking, index) => (
                            <div
                              key={index}
                              className="upcoming-booking p-2 border"
                            >
                              <h5 style={{ color: "#55ad9b" }}>
                                {booking.pitch_name}
                              </h5>
                              <span>
                                <b>Timing:</b> {parseDateTimeToTime(booking.start_time)}<b> -</b> {parseDateTimeToTime(booking.end_time)}
                                <br />
                              </span>
                              <span>
                                <b>Date:</b> {booking.booking_date}
                                <br />
                              </span>
                              <span>
                                <b>Booked By:</b> {booking.user_email}
                                <br />
                              </span>
                              <span>
                                <b>Contact:</b> {booking.user_contact_no}
                                <br />
                              </span>
                              <span>
                                <b>Status:</b>{" "}
                                {booking.payment_status ? (
                                  // If "payment" has a value, display it as plain text
                                  <span>{booking.payment_status}</span>
                                ) : (
                                  // If "payment" is empty, show two Unpaid
                                  <span>{"UnPaid"}</span>
                                )}
                                <br />
                              </span>
                            </div>
                          ))
                        ) : (
                          <p>No bookings found for the selected date.</p>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </>
        )}
    </>
  );
}
